import React, { useEffect } from "react";
import "./Modal.scss";
import Form from "../Formulario/Form";
import { TWEETS_STORAGE } from "../../utils/contants";

const Modal = (props) => {
  const { modalOpen } = props;

  useEffect(() => {
    const allTweetStorage = localStorage.getItem(TWEETS_STORAGE);
    const allTweetArray = JSON.parse(allTweetStorage);
    console.log(allTweetArray);
  }, []);

  if (modalOpen === false) {
    return null;
  } else {
    return (
      <div className="div2">
        <Form closeModal={props.closeModal} />
      </div>
    );
  }
};

export default Modal;
