import React, { useState, Component } from "react";
import "./Button.scss";
import Modal from "../Modal/Modal";
import Form from "../Formulario/Form";
import useClickout from "react-use-clickout";

const Button = () => {
  const [modalOpen, setModal] = useState(false);
  const [closeModal, bindHandler] = useClickout();

  const openModal = () => {
    if (modalOpen == false) {
      setModal(true);
    } else {
      setModal(false);
    }
  };

  bindHandler(() => {
    setModal(false);
  });

  const sendTweet = (event) => {
    event.preventDefault();
  };

  return (
    <div ref={closeModal}>
      <button onClick={() => openModal()} className="button1">
        <i className="fas fa-retweet" id="logoButton"></i>
      </button>

      <Modal modalOpen={modalOpen}>
        <Form sendTweet={sendTweet} />
      </Modal>
    </div>
  );
};

export default Button;
