import React from "react";
import "./Modal.scss";
import Form from "../Form/Form";
import TweetModal from "../TweetModal/TweetModal";

const Modal = (props) => {
  const [enviado, setEnviado] = useState(false);
  const { modalOpen } = props;

  if (modalOpen === false) {
    return null;
  } else {
    return (
      <div className="div2">
        <Form closeModal={props.closeModal} setAllTweets={props.setAllTweets} />
        <TweetModal enviado={enviado} />
      </div>
    );
  }
};

export default Modal;
