import React, { useState } from "react";
import "./Button.scss";
import Modal from "../Modal/Modal";

const Button = () => {
  const [tipo, setTipo] = useState("button1");

  const pushButton = () => {
    setTipo("button1 button1_red");
    const interval = setInterval(() => {
      setTipo("button1");
    }, 1000);
  };

  const modal = () => {
    state = {
      show: false,
    };
  };

  return (
    <div className="div1">
      <button
        onClick={() => {
          pushButton();
          modal();
        }}
        className={tipo}
      >
        <i className="fab fa-twitter"></i>
      </button>

      <Modal>
        <p>Contenido</p>
      </Modal>
    </div>
  );
};

export default Button;
