import React from "react";
import "./Modal.scss";
import Form from "../Formulario/Form";

const Modal = (props) => {
  const { closeModal } = props;
  const { modalOpen } = props;

  if (modalOpen === false) {
    return null;
  } else {
    return (
      <div className="div2">
        <Form closeModal={closeModal} allTweets={props.allTweets} />
      </div>
    );
  }
};

export default Modal;
