import React, { useState } from "react";
import "./Button.scss";
import Modal from "../Modal/Modal";
import Form from "../Formulario/Form";
import { ClickOutside } from "click-outside-react";

const Button = () => {
  const [modalOpen, setModal] = useState(false);
  const [closeModal, setCloseModal] = useState(false);

  const openModal = () => {
    if (modalOpen == false) {
      setModal(true);
    } else {
      setModal(false);
    }
  };

  const probando = () => {
    setCloseModal(true);
    console.log(closeModal);
  };

  const probando2 = () => {
    setCloseModal(false);
    console.log(closeModal);
  };

  const sendTweet = (event) => {
    event.preventDefault();
    console.log("Tweet Enviado");
  };

  return (
    <div OutsideClick={alert("al fin")}>
      <button onClick={() => openModal()} className="button1">
        <i className="fas fa-retweet" id="logoButton"></i>
      </button>
      <div onMouseOut={() => probando2()} onMouseOver={() => probando()}>
        <Modal modalOpen={modalOpen}>
          <Form sendTweet={sendTweet} />
        </Modal>
      </div>
    </div>
  );
};

export default Button;
