import React from "react";
import "./Modal.scss";

const Modal = (props) => {
  const { modalOpen, children } = props;

  const colorModal = document.getElementById("div2");

  document.getElementById("div2").style = { color: "red" };

  if (modalOpen === false && colorModal == "blue") {
    return alert("azul");
  }
  {
    return <div className="div2">{children}</div>;
  }
};

export default Modal;
