import React, { useState } from "react";
import "./Button.scss";
import Modal from "../Modal/Modal";

const Button = () => {
  const [tipo, setTipo] = useState("button1");
  const [modalIsOpen, setModal] = useState(false);

  const pushButton = () => {
    setTipo("button1 button1_red");
    const interval = setInterval(() => {
      setTipo("button1");
    }, 1000);
  };

  const openModal = () => {
    setModal(true);
  };

  const closeModal = () => {
    setModal(false);
  };

  return (
    <div className="div1">
      <button onClick={() => openModal()} className={tipo}>
        <i className="fab fa-twitter"></i>
      </button>
    </div>
  );
};

export default Button;

const container = document.createElement("div");
document.body.appendChild(container);
