import React, { useState, useEffect } from "react";
import "./App.css";
import Header from "./Components/Header/Header";
import Button from "./Components/Button/Button";
import Modal from "./Components/Modal/Modal";
import Base from "./Components/Modal/base";
import { TWEETS_STORAGE } from "./utils/contants";

function App() {
  const [modalOpen, setModal] = useState(false);

  const closeModal = () => {
    setModal(false);
  };

  const [todos, setTodos] = useState([]);

  useEffect(() => {
    const allTweetStorage = localStorage.getItem(TWEETS_STORAGE);
    const allTweetArray = JSON.parse(allTweetStorage);
    setTodos(allTweetArray);
  }, []);

  return (
    <div>
      <Base closeModal={closeModal} />
      <Header />
      <Button setModal={setModal} modalOpen={modalOpen} />
      <Modal
        modalOpen={modalOpen}
        closeModal={closeModal}
        allTweetArray={allTweetArray}
      />
    </div>
  );
}

export default App;
