import React from "react";
import "./TweetModal.scss";

const TweetModal = (props) => {
  const { enviado } = props;

  function close() {
    setTimeout(
      () => <div className="TweetEnviado">Tweet Enviado Correctamente</div>,
      3000
    );
  }

  if (enviado == true) {
    console.log("se envio");

    return close();
  } else {
    console.log("no se envio");
    return null;
  }
};

export default TweetModal;
