import React, { useState, Component } from "react";
import "./Button.scss";
import Modal from "../Modal/Modal";
import Form from "../Formulario/Form";
import useClickout from "react-use-clickout";

const Button = () => {
  const [modalOpen, setModal] = useState(false);
  const [closeModal, setCloseModal] = useClickout();

  const openModal = () => {
    if (modalOpen == false) {
      setModal(true);
    } else {
      setModal(false);
    }
  };

  const sendTweet = (event) => {
    event.preventDefault();
    console.log("Tweet Enviado");
  };

  setCloseModal(() => {
    setModal(false);
  });

  return (
    <div>
      <div ref={closeModal}>
        <button onClick={() => openModal()} className="button1">
          <i className="fas fa-retweet" id="logoButton"></i>
        </button>

        <Modal modalOpen={modalOpen}>
          <Form sendTweet={sendTweet} />
        </Modal>
      </div>
    </div>
  );
};

export default Button;
