import React, { useState } from "react";
import "./Form.scss";
import moment from "moment";
import { TWEETS_STORAGE } from "../../utils/contants";

const Form = (props) => {
  const { closeModal } = props;

  const [formValue, setFormValue] = useState({
    name: "",
    tweet: "",
  });

  const [tweetsArray, setTweetsArray] = useState([]);

  const SendTweet = (event, formValue) => {
    event.preventDefault();
    const { name, tweet } = formValue;

    if (!name || !tweet) {
      console.log("Todos los campos son obligatorios");
    } else {
      formValue.time = moment();
      setTweetsArray(formValue);
      localStorage.setItem(TWEETS_STORAGE, JSON.stringify(arrays));
      console.log("tweet enviado correctamente");
      closeModal();
    }

    arrays = [];
  };

  const [allTweets, setAllTweets] = useState([]);

  const allTweetStorage = localStorage.getItem(TWEETS_STORAGE);
  const allTweetArray = JSON.parse(allTweetStorage);
  setAllTweets(allTweetArray);

