import React, { useEffect, useState } from "react";
import "./Modal.scss";
import Form from "../Formulario/Form";
import { TWEETS_STORAGE } from "../../utils/contants";

const Modal = (props) => {
  const { modalOpen, todos } = props;

  if (modalOpen === false) {
    return null;
  } else {
    return (
      <div className="div2">
        <Form closeModal={props.closeModal} allTweetArray={todos} />
      </div>
    );
  }
};

export default Modal;
