import React, { useState } from "react";
import "./Button.scss";
import Modal from "../Modal/Modal";

const Button = () => {
  const [tipo, setTipo] = useState("button1");
  const [state, setState] = useState({ show: false });

  const pushButton = () => {
    setTipo("button1 button1_red");
    const interval = setInterval(() => {
      setTipo("button1");
    }, 1000);
  };

  const showModal = () => {
    setState({ show: true });
  };

  const hideModal = () => {
    setState({ show: false });
  };

  return (
    <div className="div1">
      <button onClick={() => showModal()} className={tipo}>
        <i className="fab fa-twitter"></i>
      </button>
    </div>
  );
};

export default Button;
