import React from "react";
import "./Modal.scss";

const Modal = (props) => {
  const { modalOpen, children, setModal } = props;

  if (modalOpen === false) {
    return null;
  }
  {
    return (
      <div className="div2" onClick={() => setModal(false)}>
        {children}
      </div>
    );
  }
};

export default Modal;
