import React, { useState } from "react";
import "./Button.scss";

const Button = () => {
  const [tipo, setTipo] = useState("button1");

  const pushButton = () => {
    setTipo("button1 button1_red");
    const interval = setInterval(() => {
      setTipo("button1");
    }, 1000);
    return () => clearInterval(interval);
  };

  return (
    <div>
      <button onClick={() => pushButton()} className={tipo}>
        <i className="fab fa-twitter"></i>
      </button>
    </div>
  );
};

export default Button;
