import React, { useState } from "react";
import "./Button.scss";
import Modal from "../Modal/Modal";

const Button = () => {
  const [tipo, setTipo] = useState("button1");
  const [modalOpen, setModal] = useState(false);

  const pushButton = () => {
    setTipo("button1 button1_red");
    const interval = setInterval(() => {
      setTipo("button1");
    }, 1000);
  };

  const openModal = () => {
    if (modalOpen == false) {
      setModal(true);
    } else {
      setModal(false);
    }
  };

  return (
    <div>
      <div>
        <button onClick={() => openModal()} className={tipo}>
          <i className="fab fa-twitter"></i>
        </button>
      </div>

      <Modal modalOpen={modalOpen}>Hola</Modal>
    </div>
  );
};

export default Button;
