import React, { useState, Component } from "react";
import "./Button.scss";
import useClickout from "react-use-clickout";

const Button = (props) => {
  const { setModal } = props;
  // const [closeModal, bindHandler] = useClickout();

  // bindHandler(() => {
  //   setModal(false);
  // });
  // ref={closeModal}

  return (
    <div>
      <button
        onClick={() => {
          setModal(true);
        }}
        className="button1"
      >
        <i className="fas fa-retweet" id="logoButton"></i>
      </button>
    </div>
  );
};

export default Button;
