import React, { useEffect } from "react";
import "./Modal.scss";
import Form from "../Form/Form";
import { TWEETS_STORAGE } from "../../utils/contants";

const Modal = (props) => {
  const { modalOpen, setAllTweets } = props;

  const Terminar = () => {
    const allT = localStorage.getItem(TWEETS_STORAGE);
    const allF = JSON.parse(allT);
    setAllTweets(allF);
  };

  if (modalOpen === false) {
    return null;
  } else {
    return (
      <div className="div2">
        Terminar();
        <Form closeModal={props.closeModal} />
      </div>
    );
  }
};

export default Modal;
